import React, { useEffect, useState } from "react";
import { auth, db } from "../../../firebase";
import { doc, getDoc, setDoc } from "firebase/firestore";
import { getStorage, ref, uploadBytes, getDownloadURL } from "firebase/storage";
import InfluencerNavbar from "../../../components/InfluencerNavbar";
import { toast } from "react-toastify";

const storage = getStorage();

const Profile = () => {
  const [profile, setProfile] = useState({
    name: "",
    bio: "",
    instagram: "",
    niche: "",
    age: "",
    location: "",
    contentTypes: "",
    followers: "",
    otherSocials: "",
    profilePhoto: "",
  });

  const [photoFile, setPhotoFile] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProfile = async () => {
      const user = auth.currentUser;
      if (!user) return;

      const docRef = doc(db, "users", user.uid);
      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        const data = docSnap.data();
        setProfile((prev) => ({
          ...prev,
          name: data.name || "",
          bio: data.bio || "",
          instagram: data.instagram || "",
          niche: data.niche || "",
          age: data.age || "",
          location: data.location || "",
          contentTypes: data.contentTypes || "",
          followers: data.followers || "",
          otherSocials: data.otherSocials || "",
          profilePhoto: data.profilePhoto || "",
        }));
      }

      setLoading(false);
    };

    fetchProfile();
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "profilePhoto" && files[0]) {
      const file = files[0];
      setPhotoFile(file);
      const previewUrl = URL.createObjectURL(file);
      setProfile((prev) => ({ ...prev, profilePhoto: previewUrl }));
    } else {
      setProfile((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSave = async () => {
    const user = auth.currentUser;
    if (!user) return;

    const requiredFields = [
      "name", "bio", "instagram", "niche", "age", "location",
      "contentTypes", "followers", "otherSocials"
    ];

    const missingField = requiredFields.find((field) => !profile[field]?.trim());
    if (missingField) {
      toast.error(`Please fill out the "${missingField}" field.`);
      return;
    }

    let profilePhotoURL = profile.profilePhoto;

    // Upload to Firebase Storage if a new file is selected
    if (photoFile) {
      try {
        const photoRef = ref(storage, `profilePhotos/${user.uid}`);
        await uploadBytes(photoRef, photoFile);
        profilePhotoURL = await getDownloadURL(photoRef);
      } catch (err) {
        toast.error("Failed to upload profile photo.");
        return;
      }
    }

    try {
      const docRef = doc(db, "users", user.uid);
      await setDoc(
        docRef,
        {
          ...profile,
          profilePhoto: profilePhotoURL,
          email: user.email,
          role: "influencer",
        },
        { merge: true }
      );
      toast.success("Profile updated successfully!");
    } catch (err) {
      toast.error("Something went wrong. Please try again.");
    }
  };

  if (loading) return <div className="p-4">Loading...</div>;

  return (
    <>
      <InfluencerNavbar />
      <div className="max-w-xl mx-auto p-6 bg-white rounded shadow mt-4">
        <h2 className="text-2xl font-bold mb-4 text-purple-600">My Profile</h2>

        <label className="block mb-1 font-medium">Profile Photo</label>
        <input
          type="file"
          name="profilePhoto"
          accept="image/*"
          onChange={handleChange}
          className="mb-3"
        />
        {profile.profilePhoto && (
          <img
            src={profile.profilePhoto}
            alt="Preview"
            className="h-24 w-24 rounded-full object-cover mb-4"
          />
        )}

        {[
          { label: "Full Name", name: "name", type: "text" },
          { label: "Bio", name: "bio", type: "textarea" },
          { label: "Instagram Handle", name: "instagram" },
          { label: "Niche", name: "niche" },
          { label: "Age", name: "age", type: "number" },
          { label: "Location", name: "location" },
          { label: "Content Types (comma-separated)", name: "contentTypes" },
          { label: "Followers Count", name: "followers", type: "number" },
          { label: "Other Social Links", name: "otherSocials" },
        ].map(({ label, name, type }) => (
          <div key={name}>
            <label className="block mb-1 font-medium">{label}</label>
            {type === "textarea" ? (
              <textarea
                className="w-full mb-3 p-2 border rounded"
                name={name}
                value={profile[name]}
                onChange={handleChange}
                required
              />
            ) : (
              <input
                className="w-full mb-3 p-2 border rounded"
                type={type || "text"}
                name={name}
                value={profile[name]}
                onChange={handleChange}
                required
              />
            )}
          </div>
        ))}

        <button
          onClick={handleSave}
          className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700 w-full"
        >
          Save Profile
        </button>
      </div>
    </>
  );
};

export default Profile;
