import React, { useState } from "react";
import InfluencerNavbar from "../../../components/InfluencerNavbar";

const dummyCampaigns = [
  {
    id: 1,
    brand: "Glowify Skincare",
    title: "Summer Skincare Launch",
    description: "Looking for skincare influencers to promote our summer product line. Includes PR box + ₹5000 payout.",
  },
  {
    id: 2,
    brand: "Zest Beverages",
    title: "Reel Challenge",
    description: "Create fun & engaging reels featuring our new sparkling drink. ₹3000 + bonus for viral videos.",
  },
  {
    id: 3,
    brand: "UrbanVerse",
    title: "Streetwear Collab",
    description: "Post 2 IG stories and 1 photo wearing our new drop. Free merch + ₹4000.",
  },
];

const Campaigns = () => {
  const [appliedCampaigns, setAppliedCampaigns] = useState([]);

  const handleApply = (id) => {
    if (!appliedCampaigns.includes(id)) {
      setAppliedCampaigns([...appliedCampaigns, id]);
    }
  };

  return (
    <>
      <InfluencerNavbar />
      <div className="p-6 max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold text-purple-600 mb-6 text-center">Available Campaigns</h1>

        <div className="space-y-6">
          {dummyCampaigns.map((campaign) => (
            <div key={campaign.id} className="border rounded-lg p-4 shadow-sm bg-white">
              <h2 className="text-xl font-semibold text-purple-700">{campaign.title}</h2>
              <p className="text-sm text-gray-500 mb-2">By {campaign.brand}</p>
              <p className="text-gray-700">{campaign.description}</p>

              <button
                onClick={() => handleApply(campaign.id)}
                disabled={appliedCampaigns.includes(campaign.id)}
                className={`mt-4 px-4 py-2 rounded ${
                  appliedCampaigns.includes(campaign.id)
                    ? "bg-gray-400 cursor-not-allowed text-white"
                    : "bg-purple-600 text-white hover:bg-purple-700"
                }`}
              >
                {appliedCampaigns.includes(campaign.id) ? "Applied" : "Apply"}
              </button>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default Campaigns;
