import React, { useEffect, useState } from "react";
import { auth, db } from "../../../firebase";
import { doc, getDoc, setDoc } from "firebase/firestore";
import InfluencerNavbar from "../../../components/InfluencerNavbar";

const Profile = () => {
  const [profile, setProfile] = useState({
    name: "",
    bio: "",
    instagram: "",
    niche: "",
    location: "",
    age: "",
    contentTypes: "",
    followers: "",
    otherLinks: "",
    collaborations: "",
    profilePhoto: ""
  });

  const [loading, setLoading] = useState(true);
  const [previewPhoto, setPreviewPhoto] = useState("");

  useEffect(() => {
    const fetchProfile = async () => {
      const user = auth.currentUser;
      if (!user) return;

      const docRef = doc(db, "users", user.uid);
      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        const data = docSnap.data();
        setProfile((prev) => ({ ...prev, ...data }));
        setPreviewPhoto(data.profilePhoto || "");
      }

      setLoading(false);
    };

    fetchProfile();
  }, []);

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handlePhotoUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setProfile((prev) => ({ ...prev, profilePhoto: reader.result }));
        setPreviewPhoto(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSave = async () => {
    const user = auth.currentUser;
    if (!user) return;

    const docRef = doc(db, "users", user.uid);
    await setDoc(docRef, { ...profile, email: user.email, role: "influencer" }, { merge: true });
    alert("Profile updated!");
  };

  if (loading) return <div className="p-4">Loading...</div>;

  return (
    <>
      <InfluencerNavbar />
      <div className="max-w-2xl mx-auto p-6 bg-white shadow-md rounded">
        <h2 className="text-3xl font-bold mb-6 text-purple-600 text-center">My Profile</h2>

        {/* Profile Photo */}
        <label className="block mb-1 font-medium text-gray-700">Profile Photo</label>
        {previewPhoto && <img src={previewPhoto} alt="Profile" className="w-28 h-28 object-cover mb-4 rounded-full" />}
        <input type="file" onChange={handlePhotoUpload} className="mb-4" />

        {/* Input Fields */}
        {[
          { label: "Full Name", name: "name", type: "text" },
          { label: "Bio", name: "bio", type: "textarea" },
          { label: "Instagram Handle", name: "instagram", type: "text" },
          { label: "Niche", name: "niche", type: "text" },
          { label: "Location", name: "location", type: "text" },
          { label: "Age", name: "age", type: "number" },
          { label: "Content Types (e.g., Reels, YouTube Shorts)", name: "contentTypes", type: "text" },
          { label: "Follower Count", name: "followers", type: "text" },
          { label: "Other Social Links", name: "otherLinks", type: "text" },
          { label: "Past Collaborations", name: "collaborations", type: "text" },
        ].map(({ label, name, type }) => (
          <div key={name} className="mb-4">
            <label className="block text-sm font-medium text-gray-700 mb-1">{label}</label>
            {type === "textarea" ? (
              <textarea
                name={name}
                value={profile[name]}
                onChange={handleChange}
                className="w-full p-2 border rounded"
              />
            ) : (
              <input
                type={type}
                name={name}
                value={profile[name]}
                onChange={handleChange}
                className="w-full p-2 border rounded"
              />
            )}
          </div>
        ))}

        <button
          onClick={handleSave}
          className="bg-purple-600 text-white px-5 py-2 rounded hover:bg-purple-700"
        >
          Save Profile
        </button>
      </div>
    </>
  );
};

export default Profile;
