import React, { useEffect, useState } from "react";
import { auth, db } from "../../../firebase";
import { doc, getDoc, setDoc } from "firebase/firestore";

import InfluencerNavbar from "../../../components/InfluencerNavbar"; // ✅ Import the navbar

const Profile = () => {
  const [profile, setProfile] = useState({
    name: "",
    bio: "",
    instagram: "",
    niche: "",
  });

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProfile = async () => {
      const user = auth.currentUser;
      if (!user) return;

      const docRef = doc(db, "users", user.uid);
      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        const data = docSnap.data();
        setProfile((prev) => ({
          ...prev,
          name: data.name || "",
          bio: data.bio || "",
          instagram: data.instagram || "",
          niche: data.niche || "",
        }));
      }

      setLoading(false);
    };

    fetchProfile();
  }, []);

  const handleChange = (e) => {
    setProfile({ ...profile, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    const user = auth.currentUser;
    if (!user) return;

    const docRef = doc(db, "users", user.uid);
    await setDoc(docRef, { ...profile, email: user.email, role: "influencer" }, { merge: true });
    alert("Profile updated!");
  };

  if (loading) return <div className="p-4">Loading...</div>;

  return (
    <>
      <InfluencerNavbar /> {/* ✅ Render the navbar here */}

      <div className="max-w-xl mx-auto p-6">
        <h2 className="text-2xl font-bold mb-4 text-purple-600">My Profile</h2>
        <input
          className="w-full mb-3 p-2 border rounded"
          type="text"
          name="name"
          placeholder="Your Name"
          value={profile.name}
          onChange={handleChange}
        />
        <textarea
          className="w-full mb-3 p-2 border rounded"
          name="bio"
          placeholder="Short bio"
          value={profile.bio}
          onChange={handleChange}
        />
        <input
          className="w-full mb-3 p-2 border rounded"
          type="text"
          name="instagram"
          placeholder="Instagram Handle"
          value={profile.instagram}
          onChange={handleChange}
        />
        <input
          className="w-full mb-3 p-2 border rounded"
          type="text"
          name="niche"
          placeholder="Niche (e.g., fashion, tech)"
          value={profile.niche}
          onChange={handleChange}
        />
        <button
          onClick={handleSave}
          className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700"
        >
          Save Profile
        </button>
      </div>
    </>
  );
};

export default Profile;
