import React, { useState } from "react";
import BrandNavbar from "../../../components/BrandNavbar";

const initialApplicants = [
  {
    id: 1,
    name: "Aarav Sharma",
    campaign: "Summer Skincare Launch",
    status: "Applied",
  },
  {
    id: 2,
    name: "Neha Desai",
    campaign: "Streetwear Collab",
    status: "Approved",
  },
  {
    id: 3,
    name: "Rohan Mehta",
    campaign: "Reel Challenge",
    status: "Applied",
  },
];

const Applicants = () => {
  const [applicants, setApplicants] = useState(initialApplicants);

  const updateStatus = (id, newStatus) => {
    const updated = applicants.map((applicant) =>
      applicant.id === id ? { ...applicant, status: newStatus } : applicant
    );
    setApplicants(updated);
  };

  return (
    <>
      <BrandNavbar />
      <div className="p-6 max-w-5xl mx-auto">
        <h1 className="text-3xl font-bold text-pink-600 text-center mb-6">Campaign Applicants</h1>

        <table className="w-full table-auto border-collapse shadow-sm">
          <thead>
            <tr className="bg-pink-100 text-pink-800 text-left">
              <th className="p-3">Influencer</th>
              <th className="p-3">Campaign</th>
              <th className="p-3">Status</th>
              <th className="p-3">Actions</th>
            </tr>
          </thead>
          <tbody>
            {applicants.map((app) => (
              <tr key={app.id} className="border-b hover:bg-pink-50">
                <td className="p-3 font-medium">{app.name}</td>
                <td className="p-3">{app.campaign}</td>
                <td className="p-3">
                  <span
                    className={`px-3 py-1 rounded-full text-sm font-semibold ${
                      app.status === "Approved"
                        ? "bg-green-200 text-green-700"
                        : app.status === "Rejected"
                        ? "bg-red-200 text-red-600"
                        : "bg-yellow-100 text-yellow-700"
                    }`}
                  >
                    {app.status}
                  </span>
                </td>
                <td className="p-3 space-x-2">
                  <button
                    onClick={() => updateStatus(app.id, "Approved")}
                    className="bg-green-500 text-white px-3 py-1 rounded hover:bg-green-600"
                  >
                    Approve
                  </button>
                  <button
                    onClick={() => updateStatus(app.id, "Rejected")}
                    className="bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600"
                  >
                    Reject
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
};

export default Applicants;
