import React, { useEffect, useState } from "react";
import BrandNavbar from "../../../components/BrandNavbar";
import { db } from "../../../firebase";
import { collection, getDocs } from "firebase/firestore";

const Applicants = () => {
  const [applications, setApplications] = useState([]);
  const [campaignsMap, setCampaignsMap] = useState({});

useEffect(() => {
  const fetchData = async () => {
    try {
      const campaignsSnapshot = await getDocs(collection(db, "campaigns"));
      const campaigns = {};
      campaignsSnapshot.forEach((doc) => {
        campaigns[doc.id] = doc.data().title;
      });
      setCampaignsMap(campaigns);

      const appsSnapshot = await getDocs(collection(db, "applications"));
      const appsData = appsSnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
        status: doc.data().status || "Pending",
      }));

      setApplications(appsData);
    } catch (error) {
      console.error("❌ Error loading applications:", error);
    }
  };

  fetchData();
}, []);

  const handleStatusChange = (id, newStatus) => {
    const updated = applications.map((app) =>
      app.id === id ? { ...app, status: newStatus } : app
    );
    setApplications(updated);
  };

  return (
    <>
      <BrandNavbar />
      <div className="p-6 max-w-5xl mx-auto">
        <h1 className="text-3xl font-bold text-pink-600 text-center mb-6">
          Campaign Applicants
        </h1>

        {applications.length === 0 ? (
          <p className="text-center text-gray-500">No applications yet.</p>
        ) : (
          <table className="w-full table-auto border-collapse shadow-sm">
          <thead>
          <tr className="bg-pink-100 text-pink-800 text-left">
          <th className="p-3">Influencer</th>
          <th className="p-3">Campaign</th>
          <th className="p-3">Applied At</th>
          <th className="p-3">Status</th>
          <th className="p-3">Action</th>
          </tr>
        </thead>

<tbody>
  {applications.map((app) => (
    <tr key={app.id} className="border-b hover:bg-pink-50">
      <td className="p-3 font-medium">{app.influencer}</td>
      <td className="p-3">{campaignsMap[app.campaignId]}</td>
      <td className="p-3 text-sm text-gray-600">
        {app.appliedAt?.toDate().toLocaleString()}
      </td>
      <td className="p-3">
        <span
          className={`px-3 py-1 rounded-full text-sm font-semibold ${
            app.status === "Approved"
              ? "bg-green-200 text-green-800"
              : app.status === "Rejected"
              ? "bg-red-200 text-red-700"
              : "bg-yellow-100 text-yellow-800"
          }`}
        >
          {app.status}
        </span>
      </td>
      <td className="p-3 space-x-2">
        <button
          onClick={() => handleStatusChange(app.id, "Approved")}
          className="px-3 py-1 bg-green-500 text-white rounded hover:bg-green-600"
        >
          Approve
        </button>
        <button
          onClick={() => handleStatusChange(app.id, "Rejected")}
          className="px-3 py-1 bg-red-500 text-white rounded hover:bg-red-600"
        >
          Reject
        </button>
      </td>
    </tr>
  ))}
</tbody>

          </table>
        )}
      </div>
    </>
  );
};

export default Applicants;
