import React from 'react';
import { Routes, Route, useLocation } from 'react-router-dom';
import Navbar from './components/Navbar';

import Home from './pages/public/Home';
import About from './pages/public/About';
import Events from './pages/public/Events';
import Login from './pages/public/Login';

import BrandHome from './pages/public/brand/Home';
import PostCampaign from './pages/public/brand/PostCampaign';
import Applicants from './pages/public/brand/Applicants';
import Payments from './pages/public/brand/Payments';

import InfluencerHome from './pages/public/influencer/Home';
import Profile from './pages/public/influencer/Profile';
import Campaigns from './pages/public/influencer/Campaigns';
import Tracker from './pages/public/influencer/Tracker';
import Earnings from './pages/public/influencer/Earnings';

function App() {
  const location = useLocation();

  // Hide Navbar on influencer/brand routes
  const hideNavbar = location.pathname.startsWith('/influencer') || location.pathname.startsWith('/brand');

  return (
    <>
      {!hideNavbar && <Navbar />}

      <Routes>
        {/* Public Routes */}
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/events" element={<Events />} />
        <Route path="/login" element={<Login />} />

        <Route path="/brand/home" element={<BrandHome />} />
        <Route path="/brand/post" element={<PostCampaign />} />
        <Route path="/brand/applicants" element={<Applicants />} />
        <Route path="/brand/payments" element={<Payments />} />

        {/* Influencer Dashboard Routes */}
        <Route path="/influencer/home" element={<InfluencerHome />} />
        <Route path="/influencer/profile" element={<Profile />} />
        <Route path="/influencer/campaigns" element={<Campaigns />} />
        <Route path="/influencer/tracker" element={<Tracker />} />
        <Route path="/influencer/earnings" element={<Earnings />} />
      </Routes>
    </>
  );
}

export default App;
