import React, { useEffect, useState } from "react";
import { auth, db } from "../../../firebase";
import { doc, getDoc, setDoc } from "firebase/firestore";
import InfluencerNavbar from "../../../components/InfluencerNavbar";

const Profile = () => {
  const [profile, setProfile] = useState({
    name: "",
    bio: "",
    instagram: "",
    niche: "",
    age: "",
    location: "",
    contentTypes: "",
    followers: "",
    otherSocials: "",
    profilePhoto: "",
  });

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProfile = async () => {
      const user = auth.currentUser;
      if (!user) return;

      const docRef = doc(db, "users", user.uid);
      const docSnap = await getDoc(docRef);

      if (docSnap.exists()) {
        const data = docSnap.data();
        setProfile((prev) => ({
          ...prev,
          name: data.name || "",
          bio: data.bio || "",
          instagram: data.instagram || "",
          niche: data.niche || "",
          age: data.age || "",
          location: data.location || "",
          contentTypes: data.contentTypes || "",
          followers: data.followers || "",
          otherSocials: data.otherSocials || "",
          profilePhoto: data.profilePhoto || "",
        }));
      }

      setLoading(false);
    };

    fetchProfile();
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    if (name === "profilePhoto") {
      const file = files[0];
      const imageUrl = URL.createObjectURL(file);
      setProfile({ ...profile, profilePhoto: imageUrl });
    } else {
      setProfile({ ...profile, [name]: value });
    }
  };

const handleSave = async () => {
  const user = auth.currentUser;
  if (!user) return;

  const requiredFields = [
    "name",
    "bio",
    "instagram",
    "niche",
    "age",
    "location",
    "contentTypes",
    "followers",
    "otherSocials",
    "profilePhoto",
  ];

  const missingField = requiredFields.find((field) => !profile[field]?.trim());

  if (missingField) {
    alert(`Please fill out the "${missingField}" field.`);
    return;
  }

  const docRef = doc(db, "users", user.uid);
  await setDoc(
    docRef,
    {
      ...profile,
      email: user.email,
      role: "influencer",
    },
    { merge: true }
  );

  alert("Profile updated!");
};


  if (loading) return <div className="p-4">Loading...</div>;

  return (
    <>
      <InfluencerNavbar />
      <div className="max-w-xl mx-auto p-6 bg-white rounded shadow">
        <h2 className="text-2xl font-bold mb-4 text-purple-600">My Profile</h2>

        <label className="block mb-1 font-medium">Profile Photo</label>
        <input
          type="file"
          name="profilePhoto"
          accept="image/*"
          onChange={handleChange}
          required
          className="mb-3"
        />
        {profile.profilePhoto && (
          <img
            src={profile.profilePhoto}
            alt="Preview"
            className="h-24 w-24 rounded-full object-cover mb-4"
          />
        )}

        <label className="block mb-1 font-medium">Full Name</label>
        <input
          className="w-full mb-3 p-2 border rounded"
          type="text"
          name="name"
          value={profile.name}
          onChange={handleChange}
          required
        />

        <label className="block mb-1 font-medium">Bio</label>
        <textarea
          className="w-full mb-3 p-2 border rounded"
          name="bio"
          value={profile.bio}
          onChange={handleChange}
          required
        />

        <label className="block mb-1 font-medium">Instagram Handle</label>
        <input
          className="w-full mb-3 p-2 border rounded"
          type="text"
          name="instagram"
          value={profile.instagram}
          onChange={handleChange}
          required
        />

        <label className="block mb-1 font-medium">Niche</label>
        <input
          className="w-full mb-3 p-2 border rounded"
          type="text"
          name="niche"
          value={profile.niche}
          onChange={handleChange}
          required
        />

        <label className="block mb-1 font-medium">Age</label>
        <input
          className="w-full mb-3 p-2 border rounded"
          type="number"
          name="age"
          value={profile.age}
          onChange={handleChange}
          required
        />

        <label className="block mb-1 font-medium">Location</label>
        <input
          className="w-full mb-3 p-2 border rounded"
          type="text"
          name="location"
          value={profile.location}
          onChange={handleChange}
          required
        />

        <label className="block mb-1 font-medium">Content Types (comma-separated)</label>
        <input
          className="w-full mb-3 p-2 border rounded"
          type="text"
          name="contentTypes"
          value={profile.contentTypes}
          onChange={handleChange}
          required
        />

        <label className="block mb-1 font-medium">Followers Count</label>
        <input
          className="w-full mb-3 p-2 border rounded"
          type="number"
          name="followers"
          value={profile.followers}
          onChange={handleChange}
          required
        />

        <label className="block mb-1 font-medium">Other Social Links</label>
        <input
          className="w-full mb-3 p-2 border rounded"
          type="text"
          name="otherSocials"
          value={profile.otherSocials}
          onChange={handleChange}
          required
        />

        <button
          onClick={handleSave}
          className="bg-purple-600 text-white px-4 py-2 rounded hover:bg-purple-700"
        >
          Save Profile
        </button>
      </div>
    </>
  );
};

export default Profile;
