import React, { useEffect, useState } from "react";
import { collection, getDocs, doc, getDoc } from "firebase/firestore";
import { auth, db } from "../../../firebase";

const InfluencerHome = () => {
  const [stats, setStats] = useState({
    appliedCount: 0,
    totalEarnings: 0,
    pendingPayments: 0,
    lastApplied: null,
  });

  useEffect(() => {
    const fetchStats = async () => {
      const user = auth.currentUser;
      if (!user) return;

      const campaignsSnapshot = await getDocs(collection(db, "campaigns"));

      let appliedCount = 0;
      let totalEarnings = 0;
      let pendingPayments = 0;
      let latestAppliedAt = null;

      for (const campaign of campaignsSnapshot.docs) {
        const applicantRef = doc(db, "campaigns", campaign.id, "applicants", user.uid);
        const applicantSnap = await getDoc(applicantRef);

        if (applicantSnap.exists()) {
          const { status, payout, paymentStatus, appliedAt } = applicantSnap.data();

          if (status === "Approved") {
            appliedCount++;
            if (paymentStatus === "Paid") {
              totalEarnings += payout;
            } else if (paymentStatus === "Pending") {
              pendingPayments += payout;
            }

            if (appliedAt?.toDate) {
              const appliedDate = appliedAt.toDate();
              if (!latestAppliedAt || appliedDate > latestAppliedAt) {
                latestAppliedAt = appliedDate;
              }
            }
          }
        }
      }

      setStats({
        appliedCount,
        totalEarnings,
        pendingPayments,
        lastApplied: latestAppliedAt
          ? latestAppliedAt.toLocaleDateString("en-IN", {
              day: "numeric",
              month: "short",
              year: "numeric",
            })
          : "N/A",
      });
    };

    fetchStats();
  }, []);

  return (
    <div className="p-10 text-center">
      <h1 className="text-3xl font-bold text-pink-600">Welcome, Influencer!</h1>
      <div className="mt-8 grid grid-cols-1 md:grid-cols-4 gap-6 text-lg font-semibold">
        <div className="bg-white rounded-2xl shadow p-4">
          <p>Campaigns Applied</p>
          <p className="text-2xl text-pink-600">{stats.appliedCount}</p>
        </div>
        <div className="bg-white rounded-2xl shadow p-4">
          <p>Total Earnings</p>
          <p className="text-2xl text-green-600">₹{stats.totalEarnings}</p>
        </div>
        <div className="bg-white rounded-2xl shadow p-4">
          <p>Pending Payments</p>
          <p className="text-2xl text-yellow-600">₹{stats.pendingPayments}</p>
        </div>
        <div className="bg-white rounded-2xl shadow p-4">
          <p>Last Applied</p>
          <p className="text-2xl text-gray-600">{stats.lastApplied}</p>
        </div>
      </div>
    </div>
  );
};

export default InfluencerHome;
