import React, { useEffect, useState } from "react";
import InfluencerNavbar from "../../../components/InfluencerNavbar";
import { db } from "../../../firebase";
import { collection, getDocs, addDoc, Timestamp } from "firebase/firestore";

const Campaigns = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [loading, setLoading] = useState(true);
  const [applied, setApplied] = useState([]);

  useEffect(() => {
    const fetchCampaigns = async () => {
      try {
        const querySnapshot = await getDocs(collection(db, "campaigns"));
        const campaignsData = querySnapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));
        setCampaigns(campaignsData);
      } catch (error) {
        console.error("Error fetching campaigns:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchCampaigns();
  }, []);

const handleApply = async (campaignId) => {
  if (!applied.includes(campaignId)) {
    try {
      // Save to Firestore
      await addDoc(collection(db, "applications"), {
  influencer: "Suha Saleem", // Replace with auth name later
  campaignId,
  appliedAt: Timestamp.now(),
  status: "Pending", // âœ… THIS LINE IS REQUIRED
});

      setApplied([...applied, campaignId]);
      alert("Successfully applied to the campaign!");
    } catch (error) {
      console.error("Error applying to campaign:", error);
      alert("Failed to apply. Try again.");
    }
  }
};


  return (
    <>
      <InfluencerNavbar />
      <div className="p-6 max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold text-purple-600 mb-6 text-center">
          Available Campaigns
        </h1>

        {loading ? (
          <p className="text-center text-gray-500">Loading campaigns...</p>
        ) : campaigns.length === 0 ? (
          <p className="text-center text-gray-400">No campaigns available.</p>
        ) : (
          <div className="space-y-6">
            {campaigns.map((campaign) => (
              <div
                key={campaign.id}
                className="border rounded-lg p-4 shadow-sm bg-white"
              >
                <h2 className="text-xl font-semibold text-purple-700">
                  {campaign.title}
                </h2>
                <p className="text-sm text-gray-500 mb-2">
                  By {campaign.brand}
                </p>
                <p className="text-gray-700 mb-2">{campaign.description}</p>
                <p className="text-sm text-gray-600 mb-2">
                  ðŸ’° Budget: â‚¹{campaign.budget} | ðŸ“… Deadline: {campaign.deadline}
                </p>

                <button
                  onClick={() => handleApply(campaign.id)}
                  disabled={applied.includes(campaign.id)}
                  className={`mt-2 px-4 py-2 rounded ${
                    applied.includes(campaign.id)
                      ? "bg-gray-400 text-white cursor-not-allowed"
                      : "bg-purple-600 text-white hover:bg-purple-700"
                  }`}
                >
                  {applied.includes(campaign.id) ? "Applied" : "Apply"}
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </>
  );
};

export default Campaigns;
